mapper.select:
select
id, username, password, nickname, email, phone, address, create_time, create_user, update_time, update_user
from sys_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and nickname = #{nickname}


        and email = #{email}


        and phone = #{phone}


        and address = #{address}


        and create_time = #{createTime}


        and create_user = #{createUser}


        and update_time = #{updateTime}


        and update_user = #{updateUser}
